import React, { useState, useEffect, useMemo } from 'react';
import { Link } from 'react-router-dom';
import { pokemonApi } from '../services/pokemonApi';
import { Pokemon, PokemonListItem, SortProperty, SortOrder } from '../types/Pokemon';
import SearchBar from './SearchBar';
import PokemonCard from './PokemonCard';
import './ListView.css';

const ListView: React.FC = () => {
  const [pokemonList, setPokemonList] = useState<PokemonListItem[]>([]);
  const [pokemonData, setPokemonData] = useState<Pokemon[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [sortProperty, setSortProperty] = useState<SortProperty>('name');
  const [sortOrder, setSortOrder] = useState<SortOrder>('asc');

  useEffect(() => {
    const fetchPokemon = async () => {
      try {
        setLoading(true);
        const response = await pokemonApi.getAllPokemon(151);
        setPokemonList(response.results);

        // Fetch detailed data for first 20 Pokemon for performance
        const detailedData = await Promise.all(
          response.results.slice(0, 20).map(async (pokemon) => {
            const id = pokemon.url.split('/').slice(-2, -1)[0];
            return await pokemonApi.getPokemonById(id);
          })
        );
        setPokemonData(detailedData);
      } catch (err) {
        setError('Failed to fetch Pokemon data');
        console.error('Error fetching Pokemon:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchPokemon();
  }, []);

  const filteredAndSortedPokemon = useMemo(() => {
    let filtered = pokemonData.filter(pokemon =>
      pokemon.name.toLowerCase().includes(searchQuery.toLowerCase())
    );

    filtered.sort((a, b) => {
      let aValue: string | number;
      let bValue: string | number;

      switch (sortProperty) {
        case 'name':
          aValue = a.name;
          bValue = b.name;
          break;
        case 'id':
          aValue = a.id;
          bValue = b.id;
          break;
        case 'height':
          aValue = a.height;
          bValue = b.height;
          break;
        case 'weight':
          aValue = a.weight;
          bValue = b.weight;
          break;
        default:
          aValue = a.name;
          bValue = b.name;
      }

      if (aValue < bValue) return sortOrder === 'asc' ? -1 : 1;
      if (aValue > bValue) return sortOrder === 'asc' ? 1 : -1;
      return 0;
    });

    return filtered;
  }, [pokemonData, searchQuery, sortProperty, sortOrder]);

  const handleSearch = (query: string) => {
    setSearchQuery(query);
  };

  const handleSortChange = (property: SortProperty) => {
    if (property === sortProperty) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortProperty(property);
      setSortOrder('asc');
    }
  };

  if (loading) {
    return <div className="loading">Loading Pokemon...</div>;
  }

  if (error) {
    return <div className="error">Error: {error}</div>;
  }

  return (
    <div className="list-view">
      <div className="controls">
        <SearchBar onSearch={handleSearch} placeholder="Search Pokemon..." />

        <div className="sort-controls">
          <label>Sort by:</label>
          <select 
            value={sortProperty} 
            onChange={(e) => handleSortChange(e.target.value as SortProperty)}
          >
            <option value="name">Name</option>
            <option value="id">ID</option>
            <option value="height">Height</option>
            <option value="weight">Weight</option>
          </select>

          <button 
            onClick={() => setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc')}
            className="sort-order-btn"
          >
            {sortOrder === 'asc' ? '↑' : '↓'}
          </button>
        </div>
      </div>

      <div className="pokemon-grid">
        {filteredAndSortedPokemon.map((pokemon) => (
          <Link key={pokemon.id} to={`/pokemon/${pokemon.id}`} className="pokemon-link">
            <PokemonCard pokemon={pokemon} />
          </Link>
        ))}
      </div>

      {filteredAndSortedPokemon.length === 0 && (
        <div className="no-results">
          No Pokemon found matching your search.
        </div>
      )}
    </div>
  );
};

export default ListView;
