import React from 'react';
import { Pokemon } from '../types/Pokemon';
import './PokemonCard.css';

interface PokemonCardProps {
  pokemon: Pokemon;
  showImage?: boolean;
}

const PokemonCard: React.FC<PokemonCardProps> = ({ pokemon, showImage = false }) => {
  const getTypeColor = (typeName: string) => {
    const colors: { [key: string]: string } = {
      normal: '#A8A878',
      fire: '#F08030',
      water: '#6890F0',
      electric: '#F8D030',
      grass: '#78C850',
      ice: '#98D8D8',
      fighting: '#C03028',
      poison: '#A040A0',
      ground: '#E0C068',
      flying: '#A890F0',
      psychic: '#F85888',
      bug: '#A8B820',
      rock: '#B8A038',
      ghost: '#705898',
      dragon: '#7038F8',
      dark: '#705848',
      steel: '#B8B8D0',
      fairy: '#EE99AC',
    };
    return colors[typeName] || '#A8A878';
  };

  return (
    <div className="pokemon-card">
      {showImage && (
        <div className="pokemon-image">
          <img 
            src={pokemon.sprites.other['official-artwork'].front_default || pokemon.sprites.front_default} 
            alt={pokemon.name}
            className="card-image"
          />
        </div>
      )}

      <div className="pokemon-info">
        <h3 className="pokemon-name">
          {pokemon.name.charAt(0).toUpperCase() + pokemon.name.slice(1)}
        </h3>
        <p className="pokemon-id">#{pokemon.id.toString().padStart(3, '0')}</p>

        <div className="pokemon-types">
          {pokemon.types.map((type, index) => (
            <span 
              key={index} 
              className="type-badge"
              style={{ backgroundColor: getTypeColor(type.type.name) }}
            >
              {type.type.name.charAt(0).toUpperCase() + type.type.name.slice(1)}
            </span>
          ))}
        </div>

        <div className="pokemon-stats">
          <div className="stat-item">
            <span className="stat-label">Height:</span>
            <span className="stat-value">{pokemon.height / 10}m</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Weight:</span>
            <span className="stat-value">{pokemon.weight / 10}kg</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PokemonCard;
