import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { pokemonApi } from '../services/pokemonApi';
import { Pokemon } from '../types/Pokemon';
import './DetailView.css';

const DetailView: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [pokemon, setPokemon] = useState<Pokemon | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [allPokemonIds, setAllPokemonIds] = useState<number[]>([]);

  useEffect(() => {
    const fetchPokemonData = async () => {
      if (!id) return;

      try {
        setLoading(true);

        // Fetch all Pokemon IDs for navigation
        if (allPokemonIds.length === 0) {
          const pokemonList = await pokemonApi.getAllPokemon(151);
          const ids = pokemonList.results.map(p => {
            const urlId = p.url.split('/').slice(-2, -1)[0];
            return parseInt(urlId);
          });
          setAllPokemonIds(ids);
        }

        const pokemonData = await pokemonApi.getPokemonById(id);
        setPokemon(pokemonData);
      } catch (err) {
        setError('Failed to fetch Pokemon data');
        console.error('Error fetching Pokemon:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchPokemonData();
  }, [id, allPokemonIds.length]);

  const getCurrentIndex = () => {
    if (!id || allPokemonIds.length === 0) return -1;
    return allPokemonIds.indexOf(parseInt(id));
  };

  const navigateToPokemon = (direction: 'prev' | 'next') => {
    const currentIndex = getCurrentIndex();
    if (currentIndex === -1) return;

    let newIndex;
    if (direction === 'prev') {
      newIndex = currentIndex > 0 ? currentIndex - 1 : allPokemonIds.length - 1;
    } else {
      newIndex = currentIndex < allPokemonIds.length - 1 ? currentIndex + 1 : 0;
    }

    navigate(`/pokemon/${allPokemonIds[newIndex]}`);
  };

  const getStatColor = (statName: string) => {
    const colors: { [key: string]: string } = {
      hp: '#ff6b6b',
      attack: '#ff8e53',
      defense: '#4ecdc4',
      'special-attack': '#45b7d1',
      'special-defense': '#96ceb4',
      speed: '#feca57',
    };
    return colors[statName] || '#ddd';
  };

  if (loading) {
    return <div className="loading">Loading Pokemon details...</div>;
  }

  if (error || !pokemon) {
    return (
      <div className="error">
        <p>Error: {error || 'Pokemon not found'}</p>
        <Link to="/" className="back-link">← Back to List</Link>
      </div>
    );
  }

  return (
    <div className="detail-view">
      <div className="detail-header">
        <Link to="/" className="back-link">← Back to List</Link>
        <div className="navigation">
          <button 
            className="nav-button prev" 
            onClick={() => navigateToPokemon('prev')}
          >
            ← Previous
          </button>
          <button 
            className="nav-button next" 
            onClick={() => navigateToPokemon('next')}
          >
            Next →
          </button>
        </div>
      </div>

      <div className="pokemon-detail">
        <div className="pokemon-image">
          <img 
            src={pokemon.sprites.other['official-artwork'].front_default || pokemon.sprites.front_default} 
            alt={pokemon.name}
            className="main-image"
          />
          <div className="image-variants">
            <img 
              src={pokemon.sprites.front_default} 
              alt={`${pokemon.name} normal`}
              className="variant-image"
            />
            <img 
              src={pokemon.sprites.front_shiny} 
              alt={`${pokemon.name} shiny`}
              className="variant-image"
            />
          </div>
        </div>

        <div className="pokemon-info">
          <h1 className="pokemon-name">
            {pokemon.name.charAt(0).toUpperCase() + pokemon.name.slice(1)}
          </h1>
          <p className="pokemon-id">#{pokemon.id.toString().padStart(3, '0')}</p>

          <div className="pokemon-types">
            {pokemon.types.map((type, index) => (
              <span key={index} className={`type-badge type-${type.type.name}`}>
                {type.type.name.charAt(0).toUpperCase() + type.type.name.slice(1)}
              </span>
            ))}
          </div>

          <div className="pokemon-stats">
            <h3>Base Stats</h3>
            <div className="stats-grid">
              {pokemon.stats.map((stat, index) => (
                <div key={index} className="stat-item">
                  <div className="stat-name">
                    {stat.stat.name.replace('-', ' ').toUpperCase()}
                  </div>
                  <div className="stat-bar">
                    <div 
                      className="stat-fill"
                      style={{ 
                        width: `${(stat.base_stat / 255) * 100}%`,
                        backgroundColor: getStatColor(stat.stat.name)
                      }}
                    />
                  </div>
                  <div className="stat-value">{stat.base_stat}</div>
                </div>
              ))}
            </div>
          </div>

          <div className="pokemon-details">
            <div className="detail-item">
              <strong>Height:</strong> {pokemon.height / 10}m
            </div>
            <div className="detail-item">
              <strong>Weight:</strong> {pokemon.weight / 10}kg
            </div>
            <div className="detail-item">
              <strong>Base Experience:</strong> {pokemon.base_experience}
            </div>
          </div>

          <div className="pokemon-abilities">
            <h3>Abilities</h3>
            <div className="abilities-list">
              {pokemon.abilities.map((ability, index) => (
                <span key={index} className="ability-badge">
                  {ability.ability.name.replace('-', ' ').toUpperCase()}
                </span>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DetailView;
